var light = angular.module('light', ['ngRoute', 'light.controllers']);

light.config(['$routeProvider', '$locationProvider', function ($routeProvider, $locationProvider) {
  $routeProvider
    .when('/', {
      templateUrl: 'views/login.html',
      controller : 'Login',
      resolve: {
      }
  })
  .when('/dashboard', {
    templateUrl: 'views/dashbard.html',
    controller : 'Dashbard'
  })
  .when('/credit', {
    templateUrl: 'views/credit.html',
    controller : 'Credit'
  })
  .otherwise({ redirectTo: '/' });
}]);

var controllers = angular.module('light.controllers', []);

//Loing
controllers.controller('Login', ['$scope', '$location', function ($scope, $location) {
  $scope.login = function () {
    $location.path('dashboard');
  };
}]);


//Dashboard
controllers.controller('Dashbard', ['$scope', '$location', function ($scope, $location) {
  $scope.balance = '40';
  $scope.buy = function () {
    $location.path('credit');
  };
}]);


//Buy Credit
controllers.controller('Credit', ['$scope', '$location', function ($scope, $location) {
  $scope.back = function () {
    $location.path('dashboard');
  };
}]);
