var light = angular.module('light', ['ngRoute', 'light.services', 'light.controllers', 'light.directives']);


//Run
light.run( function($rootScope, $location, Local) {
  $rootScope.$on('$routeChangeStart', function(event, next, current) {
    var user = Local.get('user');

    if (!next.public && !user) {
      $location.path('login');
    }
  });
});


//Confing
light.config(['$httpProvider', '$routeProvider', '$locationProvider', function ($httpProvider, $routeProvider, $locationProvider) {
  $httpProvider.interceptors.push('httpInterceptor');

  $routeProvider
    .when('/', {
      templateUrl: 'views/login.html',
      controller : 'Login',
      public     : true
    })
    //Dashboard
    .when('/dashboard', {
      templateUrl: 'views/dashbard.html',
      controller : 'Dashbard',
      public: false,
      resolve: {
        balance: ['Local', 'LoginService', '$rootScope', function (Local, LoginService, $rootScope) {
          var user = Local.get('user');

          if (user) {
            LoginService.send({
              email: user.email,
              pass : user.pass
            })
            .success(function (data) {
              Local.set('user', data);
              $rootScope.user = data;
            });
          }
        }]
      }
    })
    //Credit
    .when('/credit', {
      templateUrl: 'views/credit.html',
      controller : 'Credit',
      public: false,
      resolve: {
        user: ['Local', 'LoginService', '$rootScope', function (Local, LoginService, $rootScope) {
          var user = Local.get('user');

          if (user) {
            LoginService.send({
              email: user.email,
              pass : user.pass
            })
            .success(function (data) {
              Local.set('user', data);
              $rootScope.user = data;
            });

          }
        }]
      }
    })
    //Credit
    .when('/debit', {
      templateUrl: 'views/debit.html',
      controller : 'Debit',
      public: false,
      resolve: {
        user: ['Local', 'LoginService', '$rootScope', function (Local, LoginService, $rootScope) {
          var user = Local.get('user');

          if (user) {
            LoginService.send({
              email: user.email,
              pass : user.pass
            })
            .success(function (data) {
              Local.set('user', data);
              $rootScope.user = data;
            });

          }
        }]
      }
    })
    .otherwise({ redirectTo: '/' });
}]);

var controllers = angular.module('light.controllers', ['ngDialog']);

//Loing
controllers.controller('Login', ['Local', 'ngDialog', '$scope', '$location', 'LoginService', function (Local, ngDialog, $scope, $location, LoginService) {
  $scope.login = function () {
    LoginService.send({
      email: $scope.email,
      pass : $scope.pass
    })
    .success(function (data) {
      if (data.error) {
        $scope.error = data.error;

        ngDialog.open({
          template: 'views/include/error.html',
          scope   : $scope
        });

      } else {
        Local.set('user', data);
        $location.path('dashboard');
      }
    });
  };
}]);


//Dashboard
controllers.controller('Dashbard', ['HistoryService','$rootScope', '$scope', '$location', '$timeout', function (HistoryService, $rootScope, $scope, $location, $timeout) {
  $timeout(function () {
    $scope.balance = $rootScope.user.balance;
  }, 100);

  HistoryService.get({ email: $rootScope.user.email })
  .success(function (data) {
    $scope.history = data.history;
  });

  $scope.goToCredit = function () {
    $location.path('credit');
  };

  $scope.goToDebit = function () {
    $location.path('debit');
  };
}]);


//Buy Credit
controllers.controller('Credit', ['ngDialog', '$rootScope', '$scope', '$location', function (ngDialog, $rootScope, $scope, $location) {
  $scope.balance = $rootScope.user.balance;

  $scope.buy = function () {
    if (!$scope.credit) {
      $scope.error = 'Selecione um valor para recarregar';

      ngDialog.open({
        template: 'views/include/error.html',
        scope   : $scope
      });

    } else {
      ngDialog.open({
        template  : 'views/include/credit-card.html',
        controller: 'CreditCard',
        scope     : $scope
      });
    }
  };

  $scope.goBack = function () {
    $location.path('dashboard');
  };
}]);


//CreditCard
controllers.controller('CreditCard', ['CreditService', '$rootScope', '$scope', '$location', function (CreditService, $rootScope, $scope, $location) {
  $scope.buy = function () {
    CreditService.update({
      balance: $rootScope.user.balance,
      email  : $rootScope.user.email,
      credit : $scope.credit
    })
    .success(function () {
      $scope.closeThisDialog();
      $location.path('dashboard');
    });
  };

  $scope.goBack = function () {
    $scope.closeThisDialog();
    $location.path('credit');
  };
}]);


//Debit Credit
controllers.controller('Debit', ['DebitService', '$rootScope', '$scope', '$location', function (DebitService, $rootScope, $scope, $location) {
  $scope.balance = $rootScope.user.balance;

  $scope.debit = function () {
    DebitService.send({
      email: $rootScope.user.email,
      balance: $rootScope.user.balance,
      kwh: $rootScope.user.kwh
    })
    .success(function () {
      $location.path('dashboard');
    });
  };

  $scope.goBack = function () {
    $location.path('dashboard');
  };
}]);

var directives = angular.module('light.directives', []);

directives.directive('loader', ['$rootScope', function ($rootScope) {
  return {
    restrict: 'E',
    link: function (scope, element, attrs) {
      scope.$on('loader:show', function () {
        return element.show();
      });

      return scope.$on('loader:hide', function () {
        return element.hide();
      });
    }
  }
}]);

var services = angular.module('light.services', []);

//Login factory
services.factory('LoginService', ['$http', function ($http) {
  var path    = '/login',
      factory = {};

  //Send data
  factory.send = function (data) {
    var login = $http.post(path, data);

    return login;
  };

  return factory;
}]);


//Credit factory
services.factory('CreditService', ['$http', function ($http) {
  var path    = '/credit',
      factory = {};

  //Update credit
  factory.update = function (data) {
    var credit = $http.post(path, data);

    return credit;
  };

  return factory;
}]);


//Debit factory
services.factory('DebitService', ['$http', function ($http) {
  var path    = '/debit',
      factory = {};

  //Debit credit
  factory.send = function (data) {
    var debit = $http.post(path, data);

    return debit;
  };

  return factory;
}]);


//Debit factory
services.factory('HistoryService', ['$http', function ($http) {
  var path    = '/history',
      factory = {};

  //History
  factory.get = function (data) {
    var history = $http.post(path, data);

    return history;
  };

  return factory;
}]);


//Local
services.factory('Local', ['$rootScope', function ($rootScope) {
  var factory = {};

  factory.set = function (name, value) {
   localStorage.setItem(name, JSON.stringify(value));
  };

  factory.get = function (name) {
    var local = localStorage.getItem(name);

    return JSON.parse(local);
  };

  return factory;
}]);


//Loader
services.factory('httpInterceptor', ['$q', '$rootScope', function ($q, $rootScope) {
  var numLoadings = 0;

  return {
    request: function (config) {

      numLoadings++;

      // Show loader
      $rootScope.$broadcast("loader:show");
      return config || $q.when(config)
    },
    response: function (response) {

      if ((--numLoadings) === 0) {
        // Hide loader
        $rootScope.$broadcast("loader:hide");
      }

      return response || $q.when(response);
    },
    responseError: function (response) {

      if (!(--numLoadings)) {
        // Hide loader
        $rootScope.$broadcast("loader:hide");
      }

      return $q.reject(response);
    }
  };
}]);
